import React from "react";
import Weather from "./app_components/weather.component.jsx";
import "bootstrap/dist/css/bootstrap.min.css";
import "weather-icons-master/css/weather-icons.css";
import Form from "./app_components/form.component";
import "./App.css";
const API_key = "e4d4431929f441568bdcc6cc7b55f8cc";
class App extends React.Component {
  constructor() {
    super();
    this.state = {
     articles: []   ,
     error : false
    };
}

getData= async (e) =>{

  fetch("http://newsapi.org/v2/top-headlines?country=gb&apiKey="+  API_key)
  .then((response) => {
    return response.json();
  })
  .then((data) => {
    this.setState({
      articles : data.articles}
    )
  }); 

}
  render() {
    return (
      <div className = "App">
        <Form 
        loadweather = {getData()}/>
       <Weather
         data ={this.state.articles}
       />
      </div>
    );
  }
}
export default App;
