import React from "react";
import Weather from "./app_components/weather.component.jsx";
import ReactDom from "react-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "weather-icons-master/css/weather-icons.css";
const API_key = "d17964bd50ad650c065c1cc255174b74";
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      city: undefined,
      country: undefined,
      tempCurrent: undefined,
      minTemp: undefined,
      maxTemp: undefined,
      description: ""
    };
    this.getWeather();
  }
  getWeather = async () => {
    const api_call = await fetch(
      "http://api.openweathermap.org/data/2.5/weather?q=London,uk&appid=d17964bd50ad650c065c1cc255174b74"
    );
    const response = await api_call.json();
    console.log(response);
    this.setState({
      city: response.name,
      country: response.sys.country,
      description: response.weather[0].description
    });
  };
  render() {
    return (
      <div className="App">
        <Weather
          city={this.state.city}
          country={this.state.country}
          description={this.state.description}
          tempCurrent={this.state.tempCurrent}
          minTemp={this.state.minTemp}
          maxTemp={this.state.maxTemp}
        />
      </div>
    );
  }
}

export default App;
